import React from 'react';
import Sidepanel from './Sidepanel/Sidepanel';
import WebSocketInstance from '../websocket';

class Chat extends React.Component {
    constructor(props){
        super(props);
        this.state = {

        }
        this.waitForSocketConnection(() => {
            WebSocketInstance.addCallbacks(this.setMessages.bind(this), this.addMessage.bind(this));
            WebSocketInstance.fetchMessages(this.props.currentUser);
        })
    }

    waitForSocketConnection = (callback) => {
        const comopnent = this;
        setTimeout(
            function(){
                if (WebSocketInstance.state() === 1){
                    console.log('connection is secure');
                    callback();
                    return;
                }
                else{
                    console.log('waiting for connection...');
                    comopnent.waitForSocketConnection(callback);
                }
            }, 100);
    }

    setMessages = (messages) => {
        this.setState({
            messages
        })
    }

    addMessage = (message) => {
        this.setState({
            messages: [...this.state.messages, message]
        })
    }

    renderMessage = (messages) => {
        const currentUser = 'admin';
        return messages.map(message => (
            <li key = {message.id} className={message.user === currentUser ? 'sent' : 'replies'}>
                <img src="http://emilcarlsson.se/assets/mikeross.png" />
                <p>{message.content}</p>
            </li>
        ))
    }

    render(){
        const messages = this.state.messages;
        return(
            <div id="frame">
                <Sidepanel />
                <div className="content">
                    <div className="contact-profile">
                        <img src="http://emilcarlsson.se/assets/harveyspecter.png" alt="" />
                        <p>username</p>
                        <div className="social-media">
                            <i className="fa fa-facebook" aria-hidden="true"></i>
                            <i className="fa fa-twitter" aria-hidden="true"></i>
                            <i className="fa fa-instagram" aria-hidden="true"></i>
                        </div>
                    </div>
                    <div className="messages">
                        <ul id="chat-log">
                            {messages && this.renderMessage(messages)}
                        </ul>
                    </div>
                    <div className="message-input">
                        <div className="wrap">
                        <input id="chat-message-input" type="text" placeholder="Write your message..." />
                        <i className="fa fa-paperclip attachment" aria-hidden="true"></i>
                        <button id="chat-message-submit" className="submit"><i className="fa fa-paper-plane" aria-hidden="true"></i></button>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Chat;
